const arr = [1, 2, 3, 4, 5];

// length: returns the length of the array
const length = arr.length;

// push: adds an element to the end of the array and returns the new length
const newLength = arr.push(6);

// pop: removes the last element from the array and returns it
const lastElement = arr.pop();

// unshift: adds an element to the beginning of the array and returns the new length
const newLength2 = arr.unshift(0);

// shift: removes the first element from the array and returns it
const firstElement = arr.shift();

// slice: returns a new array that contains a portion of the original array
const slicedArray = arr.slice(2, 4);

// splice: removes or replaces elements from the array and returns the removed elements
const removedElements = arr.splice(1, 2, 'a', 'b', 'c');

// forEach: executes a provided function once for each array element
numbers.forEach((number) => console.log(number));

// map: creates a new array with the results of calling a provided function on every element in the array
const doubled = numbers.map((number) => number * 2);

// filter: creates a new array with all elements that pass the test implemented by the provided function
const filtered = numbers.filter((number) => number > 3);

// reduce: applies a function against an accumulator and each element in the array to reduce it to a single value
const reduced = numbers.reduce((accumulator, currentValue) => accumulator + currentValue);
