let num = 10;
if (num > 0) {
 console.log('The number is positive.');
}

let day = 'Monday';
switch (day) {
 case 'Monday':
 console.log('Today is Monday.');
 break;
 case 'Tuesday':
 console.log('Today is Tuesday.');
 break;
 default:
 console.log('Today is not Monday or Tuesday.');
}
